import React, {useState} from 'react';
import './App.css';


const api = {
  key: "7b1edf2df1811335672c8112a8156033",
  base: "https://api.openweathermap.org/data/2.5/"
}



function App() {
  const [query, setQuery] = useState('');
  const [weather, setWeather] = useState({});

  const search = evt => {
    if (evt.key === "Enter") {
      fetch(`${api.base}weather?q=${query}&units=imperial&APPID=${api.key}`)
        .then(res => res.json())
        .then(result => {
          setWeather(result);
          setQuery('');
          console.log(result);
        });
    }
  }
const dateBuilder = (d) => {
  let months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
    let days = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];

    let day = days[d.getDay()];
    let date = d.getDate();
    let month = months[d.getMonth()];
    let year = d.getFullYear();

    return `${day}\, ${month} ${date}\, ${year}`
}

  return(
    <div className = "app">
      <div className={(typeof weather.main != "undefined") ? ((weather.main.temp > 13) ? 'app.warm' : 'app') : 'app'}></div>
      <main>
        <div className = "search-box">
          <input type = "text" className = "search-bar" placeholder = "Search..."  onChange = {e => setQuery(e.target.value)} 
          value = {query} onKeyPress = {search}/>
        </div>
        {(typeof weather.main != "undefined") ? (
        <div>
          <div className="location-box">
            <div className="location">{weather.name}, {weather.sys.country}</div>
            <div className="date">{dateBuilder(new Date())}</div>
          </div>
          <div className="weather-box">
            <div className="temp">
              {Math.round(weather.main.temp)}Â°F
            </div>
          </div>
          <div className="weather">{weather.weather[0].main}</div>
        </div>
        ) : ('')}
      </main>
    </div>
  );
}

export default App;
